function getRandomBgrImage(last) {
  let rnd = last
  while (rnd === last) {
    rnd = Math.floor(Math.random() * 21)
  }

  let bgrStr = rnd.toString()

  if (bgrStr.length === 1) {
    bgrStr = "0" + bgrStr
  }
  return "qrc:/res/images/backgrounds/bgr" + bgrStr + ".jpg"
}

function fillModelFromLevels(m_model, m_level) {
  let start_index = m_level * CELL_COUNT
  m_model.clear()

  for (var i = 0; i < CELL_COUNT; i++) {
    m_model.append({
                     "cell": m_levels[start_index + i],
                     "delayWin": m_effects[i] * 300,
                     "delayLose": m_effects[CELL_COUNT + i] * 2000,
                     "m_state": getState(m_levels[start_index + i])
                   })
  }
}

function clickOnTile(m_model, x_pos, y_pos) {

  let m_index = -1

  /// @note click Cell
  m_index = x_pos + (y_pos * 5)
  setCellValue(m_model, m_index)

  /// @note click Cell left on the cell
  if ((x_pos - 1) >= 0) {
    m_index = (x_pos - 1) + (y_pos * 5)
    setCellValue(m_model, m_index)
  }

  /// @note click Cell right on the cell
  if ((x_pos + 1) < 5) {
    m_index = (x_pos + 1) + (y_pos * 5)
    setCellValue(m_model, m_index)
  }

  /// @note click Cell up on the cell
  if ((y_pos - 1) >= 0) {
    m_index = x_pos + ((y_pos - 1) * 5)
    setCellValue(m_model, m_index)
  }
  /// @note click Cell down on the cell
  if ((y_pos + 1) < 5) {
    m_index = x_pos + ((y_pos + 1) * 5)
    setCellValue(m_model, m_index)
  }
}

function isWinGame(m_model) {
  for (var i = 0; i < m_model.rowCount(); i++) {
    if (m_model.get(i).cell === 1) {
      return false
    }
  }
  return true
}

function setCellValue(m_model, m_index) {
  let m_value = (m_model.get(m_index).cell) ? 0 : 1
  m_model.setProperty(m_index, "cell", m_value)
  m_model.setProperty(m_index, "m_state", getState(m_value))
}

function getState(m_value) {
  let m_state

  switch (m_value) {
  case 0:
    m_state = "lightOFF"
    break
  case 1:
    m_state = "lightON"
    break
  case 2:
    m_state = "lightBLK"
    break
  default:
    m_state = "lightOFF"
  }
  return m_state
}
